export const initialState = {
    username: "guest",
    cart: 0
}
const rootReducer = (state={}, action) => {
    switch(action.type) {
        case "ADD_PRODUCT" : {
            return state
        }

        case "SET_USER" : {
            const username = action.value;
            return {
                ...state,
                username: username
            };
        }

        case "ADD_CART" : {
            return {
                ...state,
                cart: state.cart+1
            }
        }

        case "DELETE_CART" : {
            return {
                ...state,
                cart : state.cart--
            }
        }

        default: return state;
    }
}

export default rootReducer;
