import React, { useEffect, useState } from "react";
import http from "./../../services/httpService";

const apiEndPoint = "http://localhost:8090/administrator/brand";

function ManageBrand() {
  const [data, setData] = useState([]);
  const [code, setCode] = useState(null);
  const [name, setName] = useState(null);

  let inputHandler = (type) => (event) => {
    if (type === "code") setCode(event.target.value);
    else setName(event.target.value);
    console.log(code, name);
  };

  let deleteItem = (id) => {
    let copyData = data.slice();
    console.log(copyData);
    // http.delete(apiEndPoint, {
    //   headers: http.getJWT(),
    //   data: {
    //     brandId: id,
    //   },
    // });

    copyData = copyData.filter((data) => data.id !== id);
    setData(copyData);
  };

  let addNew = () => {
    http
      .post(
        apiEndPoint,
        {
          code: code,
          name: name,
        },
        {
          headers: http.getJWT(),
        }
      )
      .then((res) => {
        setData(data.concat(res.data));
        alert(res.data.name + " successfully added.");
      })
      .catch((err) => console.log(err));

    setCode(null);
    setName(null);
  };

  useEffect(() => {
    http
      .get(apiEndPoint, {
        headers: http.getJWT(),
      })
      .then((res) => setData(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      <h5>Create new Brand</h5>
      <hr />

      <div className="row">
        <div className="col-md-4">
          <input
            type="text"
            className="form-control"
            placeholder="Brand code"
            onChange={inputHandler("code")}
          />
        </div>
        <div className="col-md-4">
          <input
            type="text"
            className="form-control"
            placeholder="Brand name"
            onChange={inputHandler("name")}
          />
        </div>
        <button className="btn btn-primary manageNew" onClick={addNew}>
          Add new
        </button>
      </div>

      <div className="table-responsive mt20">
        <table className="table">
          <thead>
            <tr>
              <th>
                <div className="p-2 px-3 text-uppercase"></div>
              </th>
              <th>
                <div className="py-2 text-uppercase">Code</div>
              </th>
              <th>
                <div className="py-2 text-uppercase">Name</div>
              </th>
              <th>
                <div className="py-2 text-uppercase">Edit</div>
              </th>
              <th>
                <div className="py-2 text-uppercase">Delete</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, index) => (
              <tr key={item.id}>
                <td>{index + 1}</td>
                <td>{item.code}</td>
                <td>{item.name}</td>
                <td>
                  <button
                    className="text-dark"
                    onClick={() => deleteItem(item.id)}
                  >
                    <i className="fa fa-pen"></i>
                  </button>
                </td>
                <td>
                  <button
                    className="text-dark"
                    onClick={() => deleteItem(item.id)}
                  >
                    <i className="fa fa-trash"></i>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ManageBrand;
